Parameters.TemplateBody:
{
  "Description": "Create VPC ECS instance",
  "Parameters": {
    "imageId": {
      "Type": "String"
    },
    "hostName": {
      "Type": "String"
    },
    "privateIp": {
      "Type": "String"
    },
    "instanceType": {
      "Type": "String"
    },
    "executionId": {
      "Type": "String"
    },
    "instancesCount": {
      "Type": "String"
    },
    "zoneId": {
      "Type": "String"
    },
    "regionId": {
      "Type": "String"
    },
    "resourcePrefix": {
      "Type": "String",
      "Default": "oos-generated"
    }
  },
  "ROSTemplateFormatVersion": "2015-09-01",
  "Outputs": {
    "ecs_instance_id": {
      "Value": {
        "Fn::GetAtt": [
          "ecs",
          "InstanceIds"
        ]
      }
    }
  },
  "Resources": {
    "vswitch": {
      "Type": "ALIYUN::ECS::VSwitch",
      "Properties": {
        "VpcId": {
          "Ref": "vpc"
        },
        "Description": {
          "Fn::Join": [
            " ",
            [
              "OOS execution id is",
              {
                "Ref": "executionId"
              }
            ]
          ]
        },
        "ZoneId": {
          "Ref": "zoneId"
        },
        "CidrBlock": "{{ converConstant.vswCidrBlock }}"
      }
    },
    "sg": {
      "Type": "ALIYUN::ECS::SecurityGroup",
      "Properties": {
        "Tags": [
          {
            "Key": "oos-generated",
            "Value": {
              "Ref": "executionId"
            }
          },
          {
            "Key": "region",
            "Value": {
              "Ref": "regionId"
            }
          }
        ],
        "VpcId": {
          "Ref": "vpc"
        },
        "SecurityGroupName": {
          "Fn::Join": [
            "-",
            [
              {
                "Ref": "resourcePrefix"
              },
              "sg"
            ]
          ]
        },
        "SecurityGroupEgress": [
          {
            "PortRange": "-1/-1",
            "Priority": 1,
            "IpProtocol": "all",
            "DestCidrIp": "0.0.0.0/0",
            "NicType": "intranet"
          }
        ]
      }
    },
    "vpc": {
      "Type": "ALIYUN::ECS::VPC",
      "Properties": {
        "CidrBlock": "{{ converConstant.vpcCidrBlock }}",
        "Description": {
          "Fn::Join": [
            " ",
            [
              "OOS execution id is",
              {
                "Ref": "executionId"
              }
            ]
          ]
        },
        "VpcName": {
          "Fn::Join": [
            "-",
            [
              {
                "Ref": "resourcePrefix"
              },
              "vpc"
            ]
          ]
        }
      }
    },
    "ecs": {
      "Type": "ALIYUN::ECS::InstanceGroup",
      "Properties": {
        "ImageId": {
          "Ref": "imageId"
        },
        "HostName": {
          "Ref": "hostName"
        },
        "PrivateIpAddress": {
          "Ref": "privateIp"
        },
        "SecurityGroupId": {
          "Ref": "sg"
        },
        "VpcId": {
          "Ref": "vpc"
        },
        "VSwitchId": {
          "Ref": "vswitch"
        },
        "InstanceType": {
          "Ref": "instanceType"
        },
        "MinAmount": {
          "Ref": "instancesCount"
        },
        "MaxAmount": {
          "Ref": "instancesCount"
        },
        "Tags": [
          {
            "Key": "oos-generated",
            "Value": {
              "Ref": "executionId"
            }
          },
          {
            "Key": "region",
            "Value": {
              "Ref": "regionId"
            }
          }
        ]
      }
    }
  }
}
